import React, { useRef, useState } from "react";
import { IoMdSearch } from "react-icons/io";
import axios from "axios";
import "../css/webBrowser.css";

const WebBrowser = () => {
  const textRef = useRef();
  const [iframe, setIFrame] = useState('');
  
  const handleChange = (event) => {
    event.preventDefault();
    textRef.current = event.target.value;
    console.log(textRef.current);
  }

  const handleSearch = async (event) => {
      // event.preventDefault();
      const resp = await axios.get("http://localhost:5000/?url=https://www.pids.gov.ph/details/news/in-the-news/educational-challenges-in-the-philippines")
      console.log(resp.data);
      return resp.data;
  }
  return (
    <div>
      <div className="frame">
        <div className="url-container">
          <div className="prompt">
            <form className="prompt-box">
            <textarea
              ref={textRef}
              onChange={handleChange}
            />
            <div className="prompt-button-container">
              <button type="submit" onClick={handleSearch}>
                <IoMdSearch />
              </button>
            </div>
            </form>
          </div>
        </div>
     
        <div dangerouslySetInnerHTML={{__html: handleSearch()}}>
          
        </div>
      </div> 
      <div>
        
      </div>
    </div>
  );
};

export default WebBrowser;
