import React, { useState, createContext, useEffect } from "react";
import { IoMdSearch } from "react-icons/io";
import axios from "axios";
import {getSite, chat } from "../../chatgpt/ai";
import "../css/webBrowser.css";

function stripHtmlTags(html) {
  // Create a new div element
  const div = document.createElement('div');
  
  // Set the HTML content of the div
  div.innerHTML = html;
  
  // Extract and return the text content of the div
  return div.textContent || div.innerText || '';
}

const WebBrowser = ({ isSidebarOpen, url, setUrl }) => {
  const [html, setHtml] = useState('');
  const [relevant, setRelevant] = useState([]);
  const [highlight, setHighlight] = useState('')
  const handleChange = (event) => {
    event.preventDefault();
    setUrl(event.target.value);
  }
  function highlightText(text, stringsToHighlight) {
    // Escape special HTML characters
    const escapeHtml = str => str.replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;').replace(/"/g, '&quot;').replace(/'/g, '&#039;');
  
    // Create a regular expression to match all strings to highlight
    const regex = new RegExp(`(${stringsToHighlight.join('|')})`, 'gi');
  
    // Replace the matched strings with highlighted HTML
    return escapeHtml(text).replace(regex, '<mark>$1</mark>');
  }
  function formatAndHighlightText(text, highlightArray) {
    // Escape any HTML special characters in the text to prevent XSS attacks
    text = text.replace(/&/g, "&amp;").replace(/</g, "&lt;").replace(/>/g, "&gt;");

    // Wrap paragraphs
    let paragraphs = text.split('\n').map(paragraph => `<p>${paragraph}</p>`).join('');

    // Highlight the specified strings
    console.log('here')
    Object.values(highlightArray).forEach((highlight) => {
        let highlightRegex = new RegExp(`(${highlight})`, 'gi'); // case-insensitive match
        paragraphs = paragraphs.replace(highlightRegex, '<mark>$1</mark>');
    });

    return paragraphs;
}
  const handleSearch = async (event) => {
      event.preventDefault();
      const resp = await axios.get(`http://localhost:5000/?url=${url}`);
      if(resp.status !== 200)
          throw Error(resp.error)
      const data = resp.data; // html
      
       const html = await getSite(`http://localhost:5000/?url=${url}`);
      // setRelevant({key1: "The Philippines, a developing country in Southeast Asia with a population of over 100 million people "})
      // Object.values(relevant).forEach(value => {
      //   const highlightedHtml = highlightText(html, value); 
      //   console.log(highlightedHtml);
      // })
      //setHtml(data);
      const testRelevant = {key1: 
        "The Philippines, a developing country in Southeast Asia with a population of over 100 million people, ",
        key2: " The Spanish government established schools that primarily catered to the Spanish elite, and education was mainly focused on religious instruction"}
        
      // setHtml(html);
      
      const highlights = formatAndHighlightText(html, testRelevant)
      setHtml(highlights)
      
      // const textOnly = stripHtmlTags(data);
      // console.log(textOnly);
      // const res = await chat(url, '');
      // res.forEach((note) => {
      //   const obj = JSON.parse(note);
      //   console.log(obj);
      //   setRelevant([...relevant, obj]);
      // });

      // relevant.forEach(str => {
      //   if(html.includes(str)){
      //     console.log(str + "includes\n\n\n");
      //   }
      // })
    
  }

  return (
    <div className={`browser-container ${isSidebarOpen ? "open" : ""}`}>
      <div className="searchBar">
        <form className="prompt-box">
          <textarea
            onChange={handleChange}/>
          <div className="prompt-button-container">
            <button type="submit" onClick={handleSearch}>
              <IoMdSearch />
            </button>
          </div>
        </form>
         <div className="scrollable" dangerouslySetInnerHTML={{__html: html}}></div> 
       {/* <div className="scrollable">
          {html}
        </div>  */}
      </div>
    </div>
  );
}
   
  export default WebBrowser;
